def vigenere_decrypt(ciphertext, key):
    plaintext = ""
    key_index = 0
    key_length = len(key)

    for char in ciphertext:
        if char.isalpha():
            # Determine shift amount based on current key character
            shift = ord(key[key_index % key_length]) - ord('A')
            
            # Decrypt the character using Vigen√®re cipher decryption formula
            decrypted_char = chr((ord(char) - shift - ord('A')) % 26 + ord('A'))
            
            plaintext += decrypted_char
            key_index += 1
        else:
            # Non-alphabetic characters remain unchanged
            plaintext += char
    
    return plaintext

# Example usage:
ciphertext = ""
key = ""
plaintext = vigenere_decrypt(ciphertext, key)
print(plaintext)